// function method is there" Hooks methods"
// number of hooks metyhods there
//1.useState()-------->similar to state---->state,setstate
//          state ---->intial informstion there ---->with in the component
// update----- setstate
// syntax------->
//             const[state,setstate]=useState() 
//2.useEffect()
//3.useContext()
//4.useRef()
// these are very importent(mejorly use)
    import {useState} from "react";     
    function Fnsignup() {
        const[information,setState]=useState("hello good morningb to all")
        const[value,setValue]=useState(0)
        const[data,setdata]=useState("")
       const change=()=>{
        setState("harikrishna technologies")
        }   
        const increment=()=>{
            setValue(value+1)
        }
        const decrement=()=>{
            setValue(value-1)
        }
        const reset=()=>{
            setValue(0)
        }
        const print=(e)=>{
            setdata(e.target.value)
        }
        
        return(
                <div>
                     <p>{information}</p>
                    
                     <button onClick={change}>update</button>
                     <br></br>
                     <h3>value:{value}</h3>
                     <br></br>
                     <button onClick={increment}>increment</button>
                     <button onClick={decrement}>decrement</button>
                     <button onClick={reset}>reset</button>
                     <br></br>
                     <br></br>
                     <input type="text" onChange={print}/>
                     <br></br>
                     
                     
                </div>
            )
            
         }











export default Fnsignup;
