import { useState } from "react";

function Empform() {
  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");

  const changeid = (e) => {
    setId(e.target.value)
  }
  const changename = (e) => {
    setName(e.target.value)
  }
  const changeemail = (e) => {
    setEmail(e.target.value)
  }
  const changemobile = (j) => {
    setMobile(j.target.value)
  }
  const formsubmit = () => {
    const data = [id, name, email, mobile]
    fetch("http://localhost:3006/employesdata", {
      method: "POST",
      header: {
        "content-type": "application/json"
      },
      body: JSON.stringify(data)
    })
      .then(() => {
        alert("succesfully saved")
      })
      .catch((err) => {
        console.log(err.message)
      })
  }
  return (
    <div>
      <form onSubmit={formsubmit}>
        <br></br>
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">
            id:-
          </label>
          <input value={id} type="number" class="form-control " id="exampleInputEmail1" aria-describedby="emailHelp" className="col-4" onChange={changeid}
          />
        </div>
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">
            name:-
          </label>
          <input
            value={name}
            type="text"
            class="form-control"
            className="col-4"
            onChange={changename}
          />
        </div>

        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">
            email:-
          </label>
          <input
            value={email}
            type="email"
            class="form-control"
            className="col-4"
            onChange={changeemail}
          />
        </div>
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">
            mobile:-
          </label>
          <input
            value={mobile}
            type="tel"
            class="form-control"
            className="col-4"
            onChange={changemobile}
          />
        </div>
        <button type="submit" class="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Empform;
