// function method is there" Hooks methods"
// number of hooks metyhods there
//1.useState()-------->similar to state---->state,setstate
//          state ---->intial informstion there ---->with in the component
// update----- setstate
// syntax------->
//             const[state,setstate]=useState() 
//2.useEffect()
//3.useContext()
//4.useRef()
// these are very importent(mejorly use)

import { useState } from "react";

function App2(){
    const [name,setname]=useState("HARIKRISHNA")
    const[other,setother]=useState("lakshmi")
    const[value,setvalue]=useState(0)
    const[info,setinfo]=useState("")
    const[fname,setfname]=useState("")
    const[email,setemail]=useState("")
    const[password,setpassword]=useState("")
    const[cpassword,setcpassword]=useState("")

  
  const changename=()=>{
    setname("varalakshmi")
  }
 const incvalue=()=>{
    setvalue(value+1)
 }  
 const decvalue=()=>{
    setvalue(value-1)
 }
 const reset=()=>{
    setvalue(0)
 }
 const current=(e)=>{
    setinfo(e.target.value)
    
   }
   const finame=(e)=>{
      fname(e.target.value)
   }
   const chaemail=(e)=>{
      fname(e.target.value)
   }
   const chapassword=(e)=>{
      fname(e.target.value)
   }
   const changeconfipass=(e)=>{
      fname(e.target.value)
   }
 const  formsubmit=()=>{
      if (fname.length<=5) {
          alert("fullname must be >5 charecters")
                   
      }
      else if(password !== cpassword){
          alert("password not match")

      }
      else {
          alert("form submitted succussfully.....!")
          localStorage.setItem("fname",finame) 
          localStorage.setItem("EMAIL",this.state.email)
          localStorage.setItem("PASSWORD",this.state.password)
          localStorage.setItem("CONFIRM PASSWORD",this.state.cpassword)
         

      }
    }







    return(
        <div>
            <h1 onClick={changename}>{name}</h1>
            <button onClick={changename}>update</button>
            <h1>value:{value}</h1>
            <button onClick={incvalue}>increse</button>
            <button onClick={decvalue}>decrese</button>
            <button onClick={reset}>reset</button> 
            <h2>type hereanything:</h2>
            <input type="text"onChange={current}></input>
            <h1>current information:{info}</h1>
           
           
             <form onSubmit={formsubmit}>
               <h3>sign up form</h3>
            <h3>fullname:</h3>
            <input value={fname} type="text"onChange={finame}/>
            <h3>email:</h3>
            <input value={email} type="email"onChange={chaemail}/>
            <h3>password:</h3>
            <input value={chapassword} type="password" onChange={chapassword}/>
            <h3>confirm password:</h3>
            <input value={cpassword} type="password" onChange={changeconfipass}/>
            <br></br><br></br>
            <input type="submit"/>

         



            </form>

           
        </div>
    )
}

export default App2;
