import React from "react"
class App14 extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            coname: "",
            ofgmail: "",
            lettersnumbers: "",
            clettersnumbers: "",
            getname: "",
            getemail: "",
            getpassword: ""

