import React from "react";

class App5 extends React.Component{
    constructor(){
        super();
        this.state={
            name:"harikrishna",
            value:"its a brand"
            

        }
    }
    componentWillMount(){
        alert(" im lerning life cycle....")
    }
render(){
    return(
        <div>
            <h1> class life cycle</h1>
            <h2>{this.state.name}{this.state.value}</h2>

        </div>
    )
}
componentDidMount(){
   setTimeout(() => {
    value:"BRANDED"
    
   }, 2000);
}

}

export default App5;
