function Empedit() {
    function Empform() {
        const [id, setId] = useState("");
        const [name, setName] = useState("");
        const [email, setEmail] = useState("");
        const [mobile, setMobile] = useState("");


        fetch("http://localhost:3006/employesdata")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
            })
            .catch((err) => {
                console.log(err.message)
            })
    }
    const navigate = useNavigate()


    const changeid = (e) => {
        setId(e.target.value)
    }
    const changename = (e) => {
        setName(e.target.value)
    }
    const changeemail = (e) => {
        setEmail(e.target.value)
    }
    const changemobile = (j) => {
        setMobile(j.target.value)
    }
    const formSubmit = () => {
        const data = { id, name, email, mobile }
        fetch("http://localhost:3006/employesdata", {
            method: "post",
            headers: {
                "content-type": "application/json"
            },
            body: JSON.stringify(data)

        })
    }

    return (
        <div>
            <form onSubmit={formSubmit}>
                <br></br>
                <div className="mb-3">
                    <label className="form-label">
                        id:-
                    </label>
                    <input type="number" id="exampleInputEmail1" aria-describedby="emailHelp" className=" form-control col-4 " value={id} onChange={changeid}
                    />
                </div>
                <div className="mb-3"> <label className="form-label">name:-  </label>
                    <input type="text" id="exampleInputEmail1" aria-describedby="emailHelp" className="col-4 form-control " value={name} onChange={changename} />
                </div>

                <div className="mb-3"> <label className="form-label"> email:- </label>
                    <input id="exampleInputEmail1" aria-describedby="emailHelp" type="email" className="col-4 form-control " value={email} onChange={changeemail} />
                </div>
                <div className="mb-3">
                    <label className="form-label">mobile:-  </label>
                    <input type="tel" id="exampleInputEmail1" aria-describedby="emailHelp" className=" col-4 form-control form-control " value={mobile} onChange={changemobile} />
                </div>
                <button type="submit" className="btn btn-primary"> Submit
                </button>
            </form>
        </div>
    )
}

export default Empedit;
