// function method is there" Hooks methods"
// number of hooks metyhods there
//1.useState()-------->similar to state---->state,setstate
//          state ---->intial informstion there ---->with in the component
// update----- setstate
// syntax------->
//             const[state,setstate]=useState() 
//2.useEffect()
//3.useContext()
//4.useRef()
// these are very importent(mejorly use)
    import {useState} from "react";     
    function Fnsignup() {
        const[information,setState]=useState("hello good morningb to all")
        const[value,setstate]=useState("0")
       const change=()=>{
        setState("harikrishna technologies")
        }   
        const incre=()=>{
            setState(value+1)
        }
        
        
        return(
                <div>
                     <p>{information}</p>
                    
                     <button onClick={change}>update</button>
                     <br></br>
                     <h3>value:{value}</h3>
                     <br></br>
                     <button onClick={incre}>increment</button>
                     <button>decrement</button>
                     <button>reset</button>

                     
                </div>
            )
            
         }











export default Fnsignup;
