import React from "react"
class App14 extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            coname: "",
            ofgmail: "",
            lettersnumbers: "",
            clettersnumbers: ""
        }
    }
    companyname = (k) => {
        this.setState({
            coname: k.target.value
        })
    }
    companygmail = (k) => {
        this.setState({
            ofgmail: k.target.value
        })
    }
    companypassword = (k) => {
        this.setState({
            lettersnumbers: k.target.value
        })
    }
    companyconfirmpassword = (k) => {
        this.setState({
            clettersnumbers: k.target.value
        })
    }
    submitform = () => {
        if (this.state.coname.length <= 5) {
            alert("plaese enter >5 charecters ")

        }
        else if (this.state.lettersnumbers !== this.state.clettersnumbers) {
            alert("please enter same password")

        }
        else {
            alert("form submitted good...!")
            localStorage.setItem("company name", this.state.coname)
            localStorage.setItem("offical gmail id", this.state.ofgmail)
            localStorage.setItem("password", this.state.lettersnumbers)
            localStorage.setItem("conform password", this.state.clettersnumbers)

        }
    }
    render() {
        return (
            <div>
                <form onSubmit={this.submitform}>
                    <label>company name:</label>
                    <input value={this.state.coname} type="text" placeholder="enter candidate name" onChange={this.companyname} /><br></br><br />
                    <label>offical gmail id:</label>
                    <input value={this.state.ofgmail} type="email" place holder="enter gmail" placeholder="enter gmail" onChange={this.companygmail} /><br></br><br />
                    <label>password:</label>
                    <input value={this.state.lettersnumbers} type="password" placeholder="enter strong password" onChange={this.companypassword} /><br></br><br />
                    <label>conform password:</label>
                    <input value={this.state.clettersnumbers} type="password" placeholder="pease enter same password" onChange={this.companyconfirmpassword} /><br></br><br />
                    <input type="button" value="Signup" />

                </form>
                <form>
                    <label>company name:</label>
                    <input type="text" placeholder="enter candidate name" /><br></br><br />
                    <label>offical gmail id:</label>
                    <input type="email" place holder="enter gmail" placeholder="enter gmail" /><br></br><br />
                    <label>password:</label>
                    <input type="password" placeholder="enter strong password" /><br></br><br />
                    <label>conform password:</label>
                    <input type="password" placeholder="pease enter same password" /><br></br><br />
                    <input type="button" value="submit" />

                </form>
            </div>
        )
    }


}
export default App14;
