import React from "react"
class Signlogin extends React.Component {

    constructor(Props) {
        super(Props)
        this.state = {
            fname: "",
            email: "",
            password: "",
            conpassword: ""

        }
    }

    chnagefname = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    chnageemail = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    chnagepassword = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    chnagecpassword = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    formsubmit = () => {
        if (this.state.fname.length >= 5) {
            alert("please < 5charecters only")
        }
        else if (this.state.password !== this.state.conpassword) {
            alert("password deos not match")

        }
        else {
            alert("form submitted suuccusfully......!")
            localStorage.setItem("NAME", this.state.fname)
            localStorage.setitem("EMAIL", this.state.email)
            localStorage.setitem("password", this.state.password)
            localStorage.setitem("conpassword", this.state.conpassword)

        }



    }


    render() {
        return (
            <div>
                <form onSubmit={this.formsubmit}>
                    <h3>Sign up Form</h3><br>
                    </br>
                    <label>NAME:</label>
                    <input type="text" value={this.state.fname} placeholder="please enter name" onChange={this.chnagefname} /><br /><br />
                    <label>EMAIL:</label>
                    <input type="email" value={this.state.email} placeholder="enter email" onChange={this.chnageemail} /><br /><br />
                    <label>PASSWORD:</label>
                    <input type="password" value={this.state.password} placeholder="enter password" onChange={this.chnagepassword} /><br /><br />
                    <label>CONFIRM PASSWORD:</label>
                    <input type="password" value={this.state.conpassword} placeholder="please enter same password" onChange={this.chnagecpassword} /><br></br><br />
                    <input type="button" value="Sign up" />


                </form>
                <form >
                    <h3>Login Form</h3>
                    <br></br>
                    <label>NAME:</label>
                    <input type="text" placeholder="please enter name" /><br />
                    <label>EMAIL:</label>
                    <input type="email" placeholder="enter email" /><br />
                    <label>PASSWORD:</label>
                    <input type="password" placeholder="enter password" /><br />
                    <label>CONFIRM PASSWORD:</label>
                    <input type="password" placeholder="please enter same password" /><br /><br />
                    <input type="button" value="login " />

                </form>
            </div>
        )
    }
}
export default Signlogin;
