class App extends React.Component{
    constructor(props){
    super(props)
    this.state={
       id:1,
       Bookname:"reactjs",
    author:"krishna",
    }
    }
   Change=()=>{
        this.setState({
            id:2,
            Bookname:"javascript",
            author:"harikrishna"        
       })
    }  
    increment(){
      this.setState({
         value:this.state+1
      })
    }

      render(){
    return(
  
        
    <div> 
        <h3>constructor method</h3>
        <p>
           id:{this.state.id}<br></br>
           Bookname:{this.state.Bookname}<br></br>
           author:{this.state.author}<br></br><br></br>
           <button onClick={this.Change}>update</button>
           <h3>value:{0}</h3>
           </p>
    </div>


    )

    }
    
}

export default App;
