import React from "react"
class Signlogin extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            fname: "",
            email: "",
            password: "",
            conpassword: ""

        }
    }
    chnagefname = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    chnageemail = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    chnagepassword = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    chnagecpassword = (e) => {
        this.setState({
            fname: e.target.value
        })
    }
    formsubmit() {

    }


    render() {
        return (
            <div>
                <from>
                    <h3>Sign up Form</h3><br>
                    </br>
                    <label>NAME:</label>
                    <input type="text" value={this.state.fname} placeholder="please enter name" onChange={this.chnagefname} /><br />
                    <label>EMAIL:</label>
                    <input type="email" value={this.state.email} placeholder="enter email" onChange={this.chnageemail} /><br />
                    <label>PASSWORD:</label>
                    <input type="password" value={this.state.password} placeholder="enter password" onChange={this.chnagepassword} /><br />
                    <label>CONFIRM PASSWORD:</label>
                    <input type="password" value={this.state.conpassword} placeholder="please enter same password" onChange={this.chnagecpassword} /><br></br><br />
                    <input type="button" value="Sign up" />


                </from>
                <from>
                    <h3>Login Form</h3>
                    <br></br>
                    <label>NAME:</label>
                    <input type="text" placeholder="please enter name" /><br />
                    <label>EMAIL:</label>
                    <input type="email" placeholder="enter email" /><br />
                    <label>PASSWORD:</label>
                    <input type="password" placeholder="enter password" /><br />
                    <label>CONFIRM PASSWORD:</label>
                    <input type="password" placeholder="please enter same password" /><br /><br />
                    <input type="button" value="login " />

                </from>
            </div>
        )
    }
}
export default Signlogin;
