import React from "react";
class App extends React.Component{
    constructor(props){
        super(props) 
        this.state={
            fname:"",
            email:"",
            pswd:"",
            cpswd:""
            
        }
    }
    changeFname=(e)=>{
        this.setState({
            fname:e.target.value
        })
    }
    changeEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    changePswd=(e)=>{
        this.setState({
            pswd:e.target.value
        })
    }
   
    changeCpswd=(e)=>{
        this.setState({
            cpswd:e.target.value
        })
    }

formSubmit=()=>{
    if(this.state.fname.length<=5){
        alert("fullname required >5 characters")
    }
    else if(this.state.pswd != this.state.cpswd){
        alert("Password does not match ..!")
    }
    else{
        alert("Form Succesfully submitted..!")
        localStorage.setItem("FullName",this.state.fname)
        localStorage.setItem("Email",this.state.email)
        localStorage.setItem("Password",this.state.pswd)
    }
}

    render(){
        return(
            <div>
                <h3>Sighn Up</h3>
                <form onSubmit={this.formSubmit}>
                    <label>FullName:</label>
                    <input value={this.state.fname} required type="text" onChange={this.changeFname}/>
                    <br/><br/>
                    <label>Email:</label>
                    <input value={this.state.email} required type="email" onChange={this.changeEmail}/>
                    <br/><br/>
                    <label>Password:</label>
                    <input value={this.state.pswd} required type="password" onChange={this.changePswd}/>
                    <br/><br/>
                    <label>Confirm Password:</label>
                    <input value={this.state.cpswd} required type="password" onChange={this.changeCpswd}/>
                    <br/><br/>
                    <input type="submit" value="Sighnup"/>
                </form>

                <h3>Login</h3>
                <form>
                    <label>FullName:</label>
                    <input type="text"/>
                    <br/><br/>
                    <label>Email:</label>
                    <input type="email"/>
                    <br/><br/>
                    <label>Password:</label>
                    <input type="password"/>
                    <br/><br/>
                    <input type="submit" value="Login"/>
                </form>
            </div>
        )
    }
}


export default App;
