import React from "react";
class App extends React.Component{
    constructor(props){
    super(props)
    this.state={
       id:1,
       book:"reactjs",
    author:"krishna",
    }
    }
   Change(){
        this.setState({
            id:2,
            Bookname:"javascript",
            author:"harikrishna"        
       })
    }  
      render(){
    return(
  
        
    <div> 
        <h3>constructor method</h3>
        <p>
           id:{this.state.id}<br></br>
           Bookname:{this.state.book}<br></br>
           author:{this.state.author}<br></br><br></br>
           <button onClick={this.Change}>update</button>
           </p>
    </div>


    )

    }
    
}

export default App;
