// function method is there" Hooks methods"
// number of hooks metyhods there
//1.useState()-------->similar to state---->state,setstate
//          state ---->intial informstion there ---->with in the component
// update----- setstate
// syntax------->
//             const[state,setstate]=useState() 
//2.useEffect()
//3.useContext()
//4.useRef()
// these are very importent(mejorly use)
    import {useState} from "react";     
    function Signlogin() {
        const[information,setState]=useState("hello good morningb to all")
        const[value,setValue]=useState(0)
        const[data,setdata]=useState("")
        const[fname,setfname]=useState("")
        const[email,setemail]=useState("")
        const[password,setpassword]=useState("")
        const[cpassword,setcpassword]=useState("")
  
        
     
     
     
     
        const change=()=>{
        setState("harikrishna technologies")
        }   
        const increment=()=>{
            setValue(value+1)
        }
        const decrement=()=>{
            setValue(value-1)
        }
        const reset=()=>{
            setValue(0)
        }
        const print=(e)=>{
            setdata(e.target.value)
        }
        const ftname=(k)=>{
         setfname(k.target.value)
        }
        
         const changefullname=(e)=>{
            setfname({
                fname:e.target.value

                
            })
           }
         const  changeemail=(e)=>{
            setemail({
                email:e.target.value
            })
           }
         const  changepass=(e)=>{
            setpassword({
                password:e.target.value
            })
            }
        
      //  const changeconfipass=(e)=>{
        //    setcpassword({
          //      cpassword:e.target.value
            //})
          const formsubmit=()=>{
                if (fname.length<=5) {
                    alert("fullname must be >5 charecters")
                             
                }
               else if(password !== cpassword){
                    alert("password not match")
    
                }
                else {
                    alert("form submitted succussfully.....!")
                    localStorage.setItem("FULLNAME",fname) 
                    localStorage.setItem("EMAIL",email)
                    localStorage.setItem("PASSWORD",password)
                    localStorage.setItem("CONFIRM PASSWORD",cpassword)
                   
    
                }
              }


        return(
                <div>
                     <p>{information}</p>
                    <button onClick={change}>update</button>
                     <br></br>
                     <h3>value:{value}</h3>
                     <br></br>
                     <button onClick={increment}>increment</button>
                     <button onClick={decrement}>decrement</button>
                     <button onClick={reset}>reset</button>
                     <br></br>
                     <br></br>
                     <input type="text" onChange={print}/>
                     <br></br>
                     <h3>current value:{data}</h3>


                   <form onSubmit={formsubmit}>
                   <h2>sign up</h2>
                   <br></br>
                   <label>FULLNAME:</label>
                   <input value={fname} required type="text" onChange={changefullname}/>
                   <br></br><br></br>
                   <label>EMAIL:</label>
                   <input value={email}required type="email" onChange={changeemail}/>
                   <br></br><br></br>
                   <label>PASSWORD:</label>
                   <input value={password} required type="password" onChange={changepass}/>
                   <br></br><br></br>
                   <label>CONFIRM PASSWORD:</label>
                   <input value={cpassword} required type="password"onChange={changeconfipass}/>
                    <br></br><br></br>
                    <input required type="submit" value="SIGN UP" onSubmit={formsubmit}/>

                    </form>

                     
                     
                </div>
            )
            
         }







        



export default Signlogin;
