import { useState } from "react";

function Empform() {
  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");

  const changeid = (e) => {
    setId(e.target.value)
  }
  const changename = (e) => {
    setName(e.target.value)
  }
  const changeemail = (e) => {
    setEmail(e.target.value)
  }
  const changemobile = (j) => {
    setMobile(j.target.value)
  }
  const handleSubmit = () => {
    const data = { id, name, email, mobile }
    fetch("", {
      method: "post",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(data)

    })

      .then(() => {
        alert("succesfully saved")
      })
      .catch((err) => {
        console.log(err.message)
      })
  }
  return (
    <div>
      <form onSubmit={formsubmit}>
        <br></br>
        <div className="mb-3">
          <label className="form-label">
            id:-
          </label>
          <input value={id} type="number" id="exampleInputEmail1" aria-describedby="emailHelp" className="col-4 form-control " onChange={changeid}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">
            name:-
          </label>
          <input
            value={name}
            type="text"

            className="col-4 form-control "
            onChange={changename}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">
            email:-
          </label>
          <input
            value={email}
            type="email"

            className="col-4 form-control "
            onChange={changeemail}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">
            mobile:-
          </label>
          <input
            value={mobile}
            type="tel"
            className=" col-4 form-control form-control "
            onChange={changemobile} />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Empform;
