class App12 extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            empid: 1,
            empname: hari,
            empcomapny: tcs
        }

    }
    render() {
        return (
            <div>
                <p>
                    {this.empid}<br></br>{this.empname}<br></br>{this.enpcompany}
                </p>

            </div>
        )
    }
}
export default App12;
